// SPDX-License-Identifier: MIT

pragma solidity 0.6.12;

import "./StorageSurveys.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC777/IERC777.sol";
import "@openzeppelin/contracts/token/ERC777/IERC777Recipient.sol";
import "@openzeppelin/contracts/token/ERC777/IERC777Sender.sol";


contract FunctionalSurveys is StorageSurveys, Ownable
{
  IERC777 private _token;
  
  constructor (IERC777 token) public {
    _token = token;
  }

// Events

  event surveyNumber(uint256 nr);
  event pollNumber(uint256 nr);

// General app functions

  function setSurvey(string memory _name, string memory _questions, uint256 _participantsAllowed, uint256 _valueOfSurvey) public payable
  {
    //saving the value of survey with 18 decimals
    uint256 _actualValueOfSurvey = _valueOfSurvey * (10 ** 18);
    //require that every user that participates at the survey gets at least 0.0001 coins
    require(_actualValueOfSurvey/_participantsAllowed >= 10 ** 14);
    address msgSender = msg.sender;
    //saving the new survey name to the array and saving the total number of surveys
    _stringArrayStorage['surveys'].push(_name);
    _uintStorage['totalSurveys']++;
    //creating a survey with it's properties
    _surveyStorage[_name]._addressStorage['surveyOwner'] = msgSender;
    _surveyStorage[_name]._stringStorage['questions'] = _questions;
    _surveyStorage[_name]._boolStorage['stoppedStatus'] = false;
    _surveyStorage[_name]._uintStorage['value'] = _actualValueOfSurvey - _actualValueOfSurvey/10;
    _surveyStorage[_name]._uintStorage['participantsAllowed'] = _participantsAllowed;
    _surveyStorage[_name]._uintStorage['totalParticipated'] = 0;
    //creating user account
    _userStorage[msgSender].surevysTitles[_userStorage[msgSender]._uintStorage['surveysCreated']] = _name;
    _userStorage[msgSender]._uintStorage['surveysCreated']++;
    //sending the rewards to the creator of the contract and setting the value of the survey
    _token.send(Ownable.owner(), _actualValueOfSurvey/10, '');
    _token.send(address(this), _surveyStorage[_name]._uintStorage['value'], '');
    //emiting the number of the survey in the array of surveys
    emit surveyNumber(_stringArrayStorage['surveys'].length - 1);
  }

  function setPoll(string memory _name, string memory _choices, uint256 _participantsAllowed, uint256 _valueOfPoll) public payable
  {
    //saving the value of survey with 18 decimals
    uint256 _actualValueOfPoll = _valueOfPoll * (10**18);
    //require that every user that participates at the survey gets at least 0.0001 coins
    require(_actualValueOfPoll/_participantsAllowed >= 10**14);
    address msgSender = msg.sender;
    //saving the new poll name to the array and saving the total number of polls
    _stringArrayStorage['polls'].push(_name);
    _uintStorage['totalPolls']++;
    //creating a poll with it's properties
    _pollStorage[_name]._addressStorage['pollOwner'] = msgSender;
    _pollStorage[_name]._stringStorage['choices'] = _choices;
    _pollStorage[_name]._boolStorage['stoppedStatus'] = false;
    _pollStorage[_name]._uintStorage['value'] = _actualValueOfPoll - _actualValueOfPoll/10;
    _pollStorage[_name]._uintStorage['participantsAllowed'] = _participantsAllowed;
    _pollStorage[_name]._uintStorage['totalParticipated'] = 0;
    //creating user account
    _userStorage[msgSender].pollTitles[_userStorage[msgSender]._uintStorage['pollsCreated']] = _name;
    _userStorage[msgSender]._uintStorage['pollsCreated']++;
    //sending the rewards to the creator of the contract and setting the value of the poll
    _token.operatorSend(msgSender, Ownable.owner(), _actualValueOfPoll/10, '', '');
    _token.operatorSend(msgSender, address(this), _pollStorage[_name]._uintStorage['value'], '', '');
    //emiting the number of the survey in the array of surveys
    emit pollNumber(_stringArrayStorage['polls'].length - 1);
  }

  function surveyParticipation(string memory _name, string memory _answers) public
  {
    //require that the survey has participation slots free and didn't stop
    require(!_surveyStorage[_name]._boolStorage['stoppedStatus']);
    //require that the user didn't participate at this survey and setting it's participation
    require(!userParticipatedSurveys[msg.sender][_name]);
    userParticipatedSurveys[msg.sender][_name] = true;
    //updating the number of participants
    _surveyStorage[_name]._uintStorage['totalParticipated']++;
    //if the free slots for participation at this survey are filled stop the contract
    if(_surveyStorage[_name]._uintStorage['totalParticipated'] == _surveyStorage[_name]._uintStorage['participantsAllowed']) _surveyStorage[_name]._boolStorage['stoppedStatus'] = true;
    //setting new answers to the survey
    _surveyStorage[_name]._stringArrayStorage['answers'].push(_answers);
    //sending the rewards for participating at the survey
    _token.operatorSend(address(this), msg.sender, _surveyStorage[_name]._uintStorage['value'] / _surveyStorage[_name]._uintStorage['participantsAllowed'], '', '');
  }

  function pollParticipation(string memory _name, string memory _choice) public
  {
    //require that the poll has participation slots free and didn't stop
    require(!_pollStorage[_name]._boolStorage['stoppedStatus']);
    //require that the user didn't participate at this poll and setting it's participation
    require(!userParticipatedPolls[msg.sender][_name]);
    userParticipatedPolls[msg.sender][_name] = true;
    //updating the number of participants
    _pollStorage[_name]._uintStorage['totalParticipated']++;
    //if the free slots for participation at this poll are filled stop the contract
    if(_pollStorage[_name]._uintStorage['totalParticipated'] == _pollStorage[_name]._uintStorage['participantsAllowed']) _pollStorage[_name]._boolStorage['stoppedStatus'] = true;
    //setting new votes to the survey
    _pollStorage[_name]._uintStorage[_choice]++;
    //sending the rewards for participating at the poll
    _token.operatorSend(address(this), msg.sender, _pollStorage[_name]._uintStorage['value'] / _pollStorage[_name]._uintStorage['participantsAllowed'], '', '');
  }

}
